const defaultState = {
  user:null,
  }

function reducer(prevState = defaultState, action){
  switch(action.type){
    case "PLAYERS":
      return {...prevState, players: action.data, room: action.data[0].game.id}
    default:
      return prevState
  }

}

export default reducer
